mapper.select:
SELECT * FROM FUNDING WHERE 1=1 

	AND ${searchType} LIKE '%'||#{keyword}||'%'


	AND CATEGORY IN (
	
		#{c}
	
	)


	AND TO_CHAR(ENROLLDATE,'YYYY-MM-DD')=#{enrollDate}


	
		AND ENDDATE < TO_CHAR(SYSDATE,'YYYYMMDD')
	
	
		AND ENDDATE >= TO_CHAR(SYSDATE,'YYYYMMDD')

SELECT COUNT(*) FROM FUNDING WHERE 1=1 

	AND ${searchType} LIKE '%'||#{keyword}||'%'


	AND CATEGORY IN (
	
		#{c}
	
	)


	AND TO_CHAR(ENROLLDATE,'YYYY-MM-DD')=#{enrollDate}


	
		AND ENDDATE < TO_CHAR(SYSDATE,'YYYYMMDD')
	
	
		AND ENDDATE >= TO_CHAR(SYSDATE,'YYYYMMDD')
