mapper.select:
		SELECT A.* , (SELECT COUNT(*) FROM FDMEMBER B WHERE A.FDNO=B.FDNO)COUNT ,
 					 (SELECT NVL(SUM(PARTPRICE),0) FROM FDMEMBER B WHERE A.FDNO = B.FDNO ) SUM 
		FROM FUNDING A WHERE STATUS = 1 AND APPR = 1 AND CATEGORY IN(#{category1}

			,#{category2}

		)
		ORDER BY ENDDATE

